var freeCite = require("./freeCite"),
	chai	 = require("chai"),
	expect   = chai.expect;

describe("freeCite", function() {
	
	it("can pull out the authors", function(done) {
		freeCite("Haselton, M., Buss, D. M., Oubaid, V., & Angleitner, A. (2005). Sex, lies, and strategic interference: The psychology of deception between the sexes. Personality and Social Psychology Bulletin 31(1), 3-23.", function(err, result) {
			expect(result.authors.join(", ")).to.equal('M Haselton, D M Buss, V Oubaid, A Angleitner');
			done();			
		})
		
	})
	
});